using System.Data.Linq;\nusing System.Linq;\n\n// Define the Parent and Child classes\npublic class Parent\n{\n    public int ID { get; set; }\n    public EntitySet<Child> Childs { get; set; }\n}\n\npublic class Child\n{\n    public int ParentID { get; set; }\n    public int ID { get; set; }\n    public string Name { get; set; }\n}\n\n// Create a DataContext\npublic class MyDataContext : DataContext\n{\n    public Table<Parent> Parents;\n    public Table<Child> Children;\n}\n\n// Use LoadWith to eager load Childs\nvar dc = new MyDataContext();\nvar parents = dc.Parents.LoadWith(p => p.Childs);\n\n// Convert Childs to a Dictionary<int, string>\nvar parentObjects = parents.Select(p => new \n{\n    ID = p.ID,\n    Childs = p.Childs.ToDictionary(c => c.ID, c => c.Name)\n});