using System;\nusing System.DirectoryServices;\n\nclass WebServiceExtension\n{\n    static void Main(string[] args)\n    {\n        // Set the path to the IIS metabase\n        string metabasePath = "IIS://localhost/W3SVC";\n\n        // Get the directory entry for the Web Service Extensions folder\n        DirectoryEntry webServiceExtensions = new DirectoryEntry(metabasePath + "/WebServiceExtensions");\n\n        // Get the ASP.NET Web Service Extension\n        DirectoryEntry aspnetExtension = webServiceExtensions.Children.Find("ASP.NET", "WebSvcExtRestrictionList");\n\n        // Enable or disable the extension\n        if (args[0].ToLower() == "enable")\n        {\n            aspnetExtension.Properties["WebSvcExtRestrictionList"].Value = 0; // 0 = Allowed\n        }\n        else\n        {\n            aspnetExtension.Properties["WebSvcExtRestrictionList"].Value = 1; // 1 = Prohibited\n        }\n\n        // Commit the changes\n        aspnetExtension.CommitChanges();\n    }\n}