using EdiNet;\n\n// Load the 837P file\nEdiDocument doc = EdiDocument.Load("path/to/file.837");\n\n// Get the ISA segment (the first segment in the file)\nIsaSegment isa = doc.Segments.OfType<IsaSegment>().FirstOrDefault();\n\n// Get the IEA segment (the last segment in the file)\nIeaSegment iea = doc.Segments.OfType<IeaSegment>().LastOrDefault();\n\n// Iterate through the segments and elements\nforeach (EdiSegment segment in doc.Segments)\n{\n    Console.WriteLine(segment.Id);\n    foreach (EdiElement element in segment.Elements)\n    {\n        Console.WriteLine(element.Id + ": " + element.Value);\n    }\n}