SmtpClient smtp = new SmtpClient(Util.get_setting("SmtpServer", ""));\nsmtp.Port = int.Parse(Util.get_setting("SmtpServerPort", ""));\n\n// Create an SSL stream\nTcpClient client = new TcpClient(Util.get_setting("SmtpServer", ""), smtp.Port);\nSslStream sslStream = new SslStream(client.GetStream(), false, ValidateServerCertificate);\n\n// Authenticate using the SSL stream\nsslStream.AuthenticateAsClient(Util.get_setting("SmtpServer", ""));\n\n// Create a new SmtpClient using the SSL stream\nsmtp = new SmtpClient();\nsmtp.Host = Util.get_setting("SmtpServer", "");\nsmtp.Port = int.Parse(Util.get_setting("SmtpServerPort", ""));\nsmtp.EnableSsl = true;\nsmtp.Credentials = new System.Net.NetworkCredential(\n    Util.get_setting("SmtpServerAuthenticateUser", ""),\n    Util.get_setting("SmtpServerAuthenticatePassword", ""));\n\n// Send the message using the SSL-enabled SmtpClient\nsmtp.Send(message);