public class MyViewModel : ViewModelBase\n{\n    private string _username;\n\n    public string Username\n    {\n        get { return _username; }\n        set\n        {\n            _username = value;\n            if (string.IsNullOrEmpty(value))\n                AddError("Username", "Username is required");\n            else\n                RemoveError("Username");\n        }\n    }\n}