public class FastStringComparer : IEqualityComparer<string>\n{\n    public bool Equals(string x, string y) => x == y;\n\n    public int GetHashCode(string s)\n    {\n        const uint prime = 16777219;\n        uint hash = 2166136261;\n\n        foreach (char c in s)\n        {\n            hash = (hash ^ c) * prime;\n        }\n\n        return (int)hash;\n    }\n}\n\npublic class FastStringSet\n{\n    private readonly HashSet<string> _set;\n\n    public FastStringSet(IEnumerable<string> strings)\n    {\n        _set = new HashSet<string>(strings, new FastStringComparer());\n    }\n\n    public bool Contains(string item) => _set.Contains(item);\n}