<%@ Control Language="C#" Inherits="System.Web.Mvc.ViewUserControl<MySite.Models.Entities.EntityFramework.Customer>" %>\n\n<% using (Html.BeginForm()) { %>\n    <%= Html.TextBoxFor(m => m.FirstName, new { id = "myform_CustFirstName" }) %>\n    <%= Html.TextBoxFor(m => m.LastName, new { id = "myform_CustLastName" }) %>\n    <%= Html.TextBoxFor(m => m.Address, new { id = "myform_CustAddr" }) %>\n\n    <input type="submit" value="Add Customer" />\n<% } %>