public void PrintLevelOrder(Node root, int level = 0)\n{\n    if (root == null) return;\n\n    if (level == 0) Console.WriteLine(root.Value);\n\n    PrintLevelOrder(root.left, level + 1);\n    PrintLevelOrder(root.right, level + 1);\n}\n\npublic void PrintTreeLevelByLevel(Node root)\n{\n    int level = 0;\n    while (true)\n    {\n        bool hasNodesAtThisLevel = false;\n        PrintLevelOrder(root, level, ref hasNodesAtThisLevel);\n        if (!hasNodesAtThisLevel) break;\n        level++;\n    }\n}\n\nprivate void PrintLevelOrder(Node root, int level, ref bool hasNodesAtThisLevel)\n{\n    if (root == null) return;\n\n    if (level == 0)\n    {\n        Console.WriteLine(root.Value);\n        hasNodesAtThisLevel = true;\n    }\n\n    PrintLevelOrder(root.left, level - 1, ref hasNodesAtThisLevel);\n    PrintLevelOrder(root.right, level - 1, ref hasNodesAtThisLevel);\n}