using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [STAThread]\n    static void Main(string[] args)\n    {\n        // Create an instance of the COM server\n        Type type = Type.GetTypeFromProgID("Python.TestServer");\n        object server = Activator.CreateInstance(type);\n\n        // Call the Hello method\n        string result = (string)server.GetType().InvokeMember("Hello", BindingFlags.InvokeMethod, null, server, new object[] { "World" });\n\n        Console.WriteLine(result);\n    }\n}