public class CustomOrderComparer : IComparer<string>\n{\n    private readonly string _priorityValue;\n\n    public CustomOrderComparer(string priorityValue)\n    {\n        _priorityValue = priorityValue;\n    }\n\n    public int Compare(string x, string y)\n    {\n        if (x == _priorityValue && y != _priorityValue) return -1;\n        if (x != _priorityValue && y == _priorityValue) return 1;\n        return string.Compare(x, y);\n    }\n}