using System.Xml;\nusing System.Xml.Xsl;\n\n// Assume 'xmlReport' is your XML report XmlDocument\nXslCompiledTransform xslt = new XslCompiledTransform();\nxslt.Load("log2xhtml.xslt"); // Load your XSLT stylesheet\n\nXmlWriterSettings settings = new XmlWriterSettings();\nsettings.Indent = true; // Pretty-print the HTML output\n\nusing (XmlWriter writer = XmlWriter.Create("report.html", settings))\n{\n    xslt.Transform(xmlReport, null, writer);\n}