public class WebServiceResult\n{\n    public bool Success { get; set; }\n    public string ErrorMessage { get; set; }\n}\n\n[WebService]\npublic class MyWebService\n{\n    [WebMethod]\n    public WebServiceResult MyMethod()\n    {\n        try\n        {\n            // Call complex functionality in C# class library\n            var result = MyClassLibrary.MyComplexMethod();\n            return new WebServiceResult { Success = true };\n        }\n        catch (Exception ex)\n        {\n            // Log the exception and return an error result\n            Logging.LogError(ex);\n            return new WebServiceResult { Success = false, ErrorMessage = ex.Message };\n        }\n    }\n}