// Server A: Generate token and redirect to Server B\n[HttpGet]\npublic ActionResult DownloadDocument(int documentId)\n{\n    // Generate a unique, time-limited token\n    var token = GenerateToken(documentId);\n\n    // Set Content-Disposition header for attachment\n    Response.AppendHeader("Content-Disposition", $"attachment; filename={documentName}");\n\n    // Redirect to Server B with token\n    return JavaScript($"window.location.href = 'https://serverb.com/download?token={token}';");\n}\n\n// Server B: Verify token and serve document\n[HttpGet]\npublic ActionResult Download(string token)\n{\n    // Verify token\n    if (!VerifyToken(token))\n    {\n        return HttpNotFound();\n    }\n\n    // Serve the document\n    var document = GetDocument(token);\n    return File(document, "application/octet-stream");\n}