using System;\nusing System.Web;\nusing Microsoft.SharePoint;\n\nnamespace PdfHandler\n{\n    public class PdfHandler : IHttpHandler\n    {\n        public void ProcessRequest(HttpContext context)\n        {\n            string filePath = context.Request.Url.AbsolutePath;\n            SPWeb web = SPContext.Current.Web;\n            SPFile file = web.GetFile(filePath);\n\n            if (file.Exists && file.Extension.ToLower() == ".pdf")\n            {\n                context.Response.ContentType = "application/pdf";\n                context.Response.AddHeader("content-disposition", "inline; filename=\"" + file.Name + "\"");\n                context.Response.AddHeader("windowtarget", "_blank");\n                context.Response.BinaryWrite(file.OpenBinary());\n                context.Response.End();\n            }\n            else\n            {\n                context.Response.StatusCode = 404;\n                context.Response.End();\n            }\n        }\n\n        public bool IsReusable\n        {\n            get { return true; }\n        }\n    }\n}