using Microsoft.Win32;\n\n// Access user-specific environment variables\nRegistryKey userEnvKey = Registry.CurrentUser.OpenSubKey("Environment");\nstring userEnvVar = (string)userEnvKey.GetValue("MyEnvVar");\n\n// Access system-wide environment variables\nRegistryKey systemEnvKey = Registry.LocalMachine.OpenSubKey(@"SYSTEM\CurrentControlSet\Control\Session Manager\Environment");\nstring systemEnvVar = (string)systemEnvKey.GetValue("MyEnvVar");