var users = new[] {\n    new { User = "Bob", Hobby = "Football" },\n    new { User = "Bob", Hobby = "Golf" },\n    new { User = "Bob", Hobby = "Tennis" },\n    new { User = "Sue", Hobby = "Sleeping" },\n    new { User = "Sue", Hobby = "Drinking" }\n};\n\nvar result = users\n    .GroupBy(u => u.User)\n    .Select(g => new {\n        User = g.Key,\n        Hobby = string.Join(", ", g.Select(u => u.Hobby))\n    });\n\nforeach (var user in result) {\n    Console.WriteLine($"{user.User} {user.Hobby}");\n}