using Microsoft.SqlServer.Dts.Runtime;\nusing System;\n\nclass SsisPackage\n{\n    public void CreatePackage()\n    {\n        Package package = new Package();\n        package.Name = "MyETLPackage";\n\n        // Create an ODBC connection manager\n        ConnectionManager odbcConnection = package.Connections.Add("ODBC");\n        odbcConnection.ConnectionString = "DSN=myODBCDataSource";\n\n        // Create a SQL Server connection manager\n        ConnectionManager sqlConnection = package.Connections.Add("OLE DB");\n        sqlConnection.ConnectionString = "Server=mySQLServer;Database=myDB;";\n\n        // Create a data flow task\n        TaskHost dataFlowTask = (TaskHost)package.Executables.Add("Data Flow Task");\n\n        // Create a data flow source (ODBC)\n        IDTSComponentMetaData100 source = dataFlowTask.InnerObject.ComponentMetaDataCollection.New();\n        source.ComponentClassID = "ODBC Source";\n        source.Name = "ODBC Source";\n\n        // Create a data flow destination (SQL Server)\n        IDTSComponentMetaData100 destination = dataFlowTask.InnerObject.ComponentMetaDataCollection.New();\n        destination.ComponentClassID = "OLE DB Destination";\n        destination.Name = "SQL Server Destination";\n\n        // Map columns and perform transformations as needed\n        // ...\n\n        package.SaveToXML("MyETLPackage.dtsx", null);\n    }\n}