public class GenericContainer<T>\n{\n    private T value;\n\n    public GenericContainer(T value)\n    {\n        this.value = value;\n    }\n\n    public T GetValue()\n    {\n        return this.value;\n    }\n}\n\n// Using the generic class with different types\nvar intContainer = new GenericContainer<int>(10);\nvar stringContainer = new GenericContainer<string>("Hello");\n\nConsole.WriteLine(intContainer.GetValue()); // Outputs: 10\nConsole.WriteLine(stringContainer.GetValue()); // Outputs: Hello