public static class LinqExtensions\n{\n    public static IQueryable<LinqToSqlModel> Where<T>(this IQueryable<LinqToSqlModel> source, Func<MyModel, bool> predicate, IModelConverter<MyModel, LinqToSqlModel> converter)\n    {\n        var linqPredicate = converter.Convert(predicate);\n        return source.Where(linqPredicate);\n    }\n\n    private static Func<LinqToSqlModel, bool> Convert(this IModelConverter<MyModel, LinqToSqlModel> converter, Func<MyModel, bool> predicate)\n    {\n        return linqModel => predicate(converter.Convert(linqModel));\n    }\n}