[DllImport("user32.dll")]\nstatic extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n[DllImport("user32.dll")]\nstatic extern void SendMessage(IntPtr hWnd, uint Msg, IntPtr wParam, IntPtr lParam);\n\nconst uint WM_KEYDOWN = 0x100;\nconst uint WM_KEYUP = 0x101;\nconst uint VK_BROWSER_FORWARD = 0xA7;\n\n// Find the Chrome window\nIntPtr hwnd = FindWindow("Chrome_WidgetWin_1", null);\n\n// Simulate the keyboard shortcut for "Forward"\nSendMessage(hwnd, WM_KEYDOWN, (IntPtr)VK_BROWSER_FORWARD, IntPtr.Zero);\nSendMessage(hwnd, WM_KEYUP, (IntPtr)VK_BROWSER_FORWARD, IntPtr.Zero);