using iTextSharp.text.pdf;\nusing System.IO;\n\npublic void SplitPdf(string filePath, string outputFolder)\n{\n    using (var pdfReader = new PdfReader(filePath))\n    {\n        for (int page = 1; page <= pdfReader.NumberOfPages; page++)\n        {\n            using (var pdfWriter = PdfWriter.GetInstance(document, new FileStream(Path.Combine(outputFolder, $"page_{page}.pdf"), FileMode.Create)))\n            {\n                pdfWriter.Open();\n                var document = new Document();\n                document.Open();\n                var cb = pdfWriter.DirectContent;\n                var pageImport = pdfWriter.GetImportedPage(pdfReader, page);\n                cb.AddTemplate(pageImport, 0, 0);\n                document.Close();\n            }\n        }\n    }\n}