using NHibernate.Mapping;\n\n// Create a new NHibernate configuration\nvar cfg = new Configuration();\n\n// Create a new dynamic mapping for the table\nvar dynamicMapping = new DynamicMapping();\ndynamicMapping.TableName = "DynamicTable1";\n\n// Add columns to the mapping\ndynamicMapping.AddColumn("ArbitraryId", NHibernateUtil.Int32);\ndynamicMapping.AddColumn("OtherColumn", NHibernateUtil.String);\n\n// Add the dynamic mapping to the configuration\ncfg.AddDynamicMapping(dynamicMapping);\n\n// Create a new session factory\nvar sessionFactory = cfg.BuildSessionFactory();\n\n// Create a new session\nvar session = sessionFactory.OpenSession();\n\n// Use the session to query the dynamic table\nvar query = session.CreateQuery("from DynamicTable1 where ArbitraryId = 2");\nvar result = query.List<object>();