// Example in C#\npublic void DeleteMasterRecord(int masterRecordId)\n{\n    // Delete Gen 3 records\n    foreach (var table3Record in db.Table3.Where(t => t.Table1ID == masterRecordId))\n    {\n        db.Table3.DeleteObject(table3Record);\n    }\n\n    // Delete Gen 2 records\n    foreach (var table1Record in db.Table1.Where(t => t.MasterRecordID == masterRecordId))\n    {\n        db.Table1.DeleteObject(table1Record);\n    }\n\n    // Delete Master Record\n    var masterRecord = db.MasterRecord.Find(masterRecordId);\n    db.MasterRecord.DeleteObject(masterRecord);\n\n    db.SaveChanges();\n}