public static class GridDefinitionExtensions\n{\n    public static readonly DependencyProperty WidthProperty = DependencyProperty.RegisterAttached(\n        "Width", typeof(GridLength), typeof(GridDefinitionExtensions),\n        new PropertyMetadata(new GridLength(1, GridUnitType.Star)));\n\n    public static readonly DependencyProperty HeightProperty = DependencyProperty.RegisterAttached(\n        "Height", typeof(GridLength), typeof(GridDefinitionExtensions),\n        new PropertyMetadata(new GridLength(1, GridUnitType.Star)));\n\n    public static void SetWidth(DependencyObject element, GridLength value)\n    {\n        element.SetValue(WidthProperty, value);\n    }\n\n    public static GridLength GetWidth(DependencyObject element)\n    {\n        return (GridLength)element.GetValue(WidthProperty);\n    }\n\n    public static void SetHeight(DependencyObject element, GridLength value)\n    {\n        element.SetValue(HeightProperty, value);\n    }\n\n    public static GridLength GetHeight(DependencyObject element)\n    {\n        return (GridLength)element.GetValue(HeightProperty);\n    }\n}