using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\npublic class AspxToHtml\n{\n    public static void ConvertAspxToHtml(string aspxUrl, string htmlFilePath)\n    {\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(aspxUrl);\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n        using (StreamReader reader = new StreamReader(response.GetResponseStream(), Encoding.UTF8))\n        {\n            string html = reader.ReadToEnd();\n\n            using (StreamWriter writer = new StreamWriter(htmlFilePath, false, Encoding.UTF8))\n            {\n                writer.Write(html);\n            }\n        }\n    }\n}