public void PopulateFullTextIndex(int fileId, byte[] fileBytes, string contentType)\n{\n    string extractedText = null;\n    switch (contentType)\n    {\n        case "application/pdf":\n            extractedText = ExtractTextFromPdf(fileBytes);\n            break;\n        case "application/vnd.openxmlformats-officedocument.wordprocessingml.document":\n            extractedText = ExtractTextFromDocx(fileBytes);\n            break;\n        case "application/msword":\n            extractedText = ExtractTextFromDoc(fileBytes);\n            break;\n    }\n\n    using (var connection = new SqlConnection("your_connection_string"))\n    {\n        connection.Open();\n        var command = new SqlCommand("INSERT INTO FileContents (FileId, ContentType, FileText) VALUES (@FileId, @ContentType, @FileText)", connection);\n        command.Parameters.AddWithValue("@FileId", fileId);\n        command.Parameters.AddWithValue("@ContentType", contentType);\n        command.Parameters.AddWithValue("@FileText", extractedText);\n        command.ExecuteNonQuery();\n    }\n}