public class SecureFieldExtender : ControlExtender\n{\n    protected override void OnInit(EventArgs e)\n    {\n        base.OnInit(e);\n        var field = (WebControl)TargetControl;\n        var attribute = (SecureFieldAttribute)field.Attributes["SecureFieldAttribute"];\n        if (attribute != null)\n        {\n            var userRoles = GetUserRoles(); // implement this method to get the user's roles\n            var orderStatus = GetOrderStatus(); // implement this method to get the order status\n            if (!attribute.AllowedRoles.Contains(userRoles) || !attribute.AllowedStatuses.Contains(orderStatus))\n            {\n                field.Enabled = false;\n            }\n        }\n    }\n}