using System.DirectoryServices;\n\npublic class ActiveDirectoryHelper\n{\n    public static void GetUserAttributes(string username)\n    {\n        // Create a new DirectoryEntry for the root of the directory\n        DirectoryEntry entry = new DirectoryEntry("LDAP://DC=yourdomain,DC=com");\n\n        // Create a DirectorySearcher to find the user\n        DirectorySearcher searcher = new DirectorySearcher(entry);\n        searcher.Filter = $"(samAccountName={username})";\n\n        // Execute the search\n        SearchResult result = searcher.FindOne();\n\n        if (result != null)\n        {\n            // Get the user's attributes\n            DirectoryEntry userEntry = result.GetDirectoryEntry();\n            string name = userEntry.Properties["givenName"].Value.ToString();\n            string telephoneId = userEntry.Properties["telephoneNumber"].Value.ToString();\n\n            // Process or display the retrieved attributes\n        }\n    }\n}