using System;\nusing System.IO;\nusing System.Threading;\n\npublic class LogWriter\n{\n    private static readonly Mutex _mutex = new Mutex(false, "LogMutex");\n\n    public void WriteLog(string message)\n    {\n        _mutex.WaitOne();\n        try\n        {\n            using (var fileStream = File.Open("log.txt", FileMode.Append, FileAccess.Write, FileShare.None))\n            {\n                using (var writer = new StreamWriter(fileStream))\n                {\n                    writer.WriteLine(message);\n                }\n            }\n        }\n        finally\n        {\n            _mutex.ReleaseMutex();\n        }\n    }\n}