using System.Data;\n\n// assuming 'adapter' is your MySqlDataAdapter object\nDataTable table = new DataTable();\nadapter.Fill(table);\n\n// loop through each row\nforeach (DataRow row in table.Rows)\n{\n    // convert Unix Epoch to human readable date\n    DateTime date = new DateTime(1970, 1, 1, 0, 0, 0, DateTimeKind.Utc).AddSeconds((int)row["unix_epoch_column"]);\n    row["unix_epoch_column"] = date.ToString("yyyy-MM-dd HH:mm:ss");\n\n    // modify other fields as needed\n    row["another_column"] = row["another_column"].ToString().ToUpper();\n}\n\n// now 'table' contains the modified data\n// you can bind it to your Repeater control\nRepeater1.DataSource = table;\nRepeater1.DataBind();