// Export Access report to RDLC\nstring reportPath = "C:\\Path\\To\\Report.accdb";\nstring rdlcPath = "C:\\Path\\To\\Report.rdlc";\n\nusing (var accessApp = new Microsoft.Office.Interop.Access.Application())\n{\n    accessApp.OpenCurrentDatabase(reportPath, false, "");\n    accessApp.DoCmd.OutputTo(Microsoft.Office.Interop.Access.AcOutputObjectType.acOutputReport, \n                             "ReportName", \n                             "RDLC", \n                             rdlcPath, \n                             false, \n                             "", \n                             0, \n                             Microsoft.Office.Interop.Access.AcExportQuality.acExportQualityPrint);\n}\n\n// View RDLC report in ReportViewer control\nReportViewer reportViewer = new ReportViewer();\nreportViewer.LocalReport.ReportPath = rdlcPath;\nreportViewer.LocalReport.Refresh();