using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Net.Http.Headers;\n\npublic class FilePoster\n{\n    public async void PostFile(byte[] fileBytes, string filename, string saveUrl, string id, string format)\n    {\n        using (var client = new HttpClient())\n        {\n            client.BaseAddress = new Uri("http://export.writer.zoho.com");\n\n            var content = new MultipartFormDataContent();\n            var fileContent = new ByteArrayContent(fileBytes);\n            fileContent.Headers.ContentType = MediaTypeHeaderValue.Parse("application/octet-stream");\n            fileContent.Headers.ContentDisposition = new ContentDispositionHeaderValue("form-data")\n            {\n                FileName = filename,\n                Name = "content"\n            };\n            content.Add(fileContent);\n\n            content.Add(new StringContent(filename), "filename");\n            content.Add(new StringContent(saveUrl), "saveurl");\n            content.Add(new StringContent(id), "id");\n            content.Add(new StringContent(format), "format");\n\n            var response = await client.PostAsync("remotedoc.im?apikey=[apikey]&output=editor", content);\n            response.EnsureSuccessStatusCode();\n        }\n    }\n}