using System.Xml.Linq;\nusing System.Xml.XPath;\n\n// Load your XML document\nXDocument doc = XDocument.Load("your_xml_file.xml");\n\n// Create an XPath evaluator that supports XPath 2.0\nvar xpath = new XPathDocument(doc.CreateReader());\nvar navigator = xpath.CreateNavigator();\nvar manager = new XmlNamespaceManager(navigator.NameTable);\nmanager.AddNamespace("fn", "http://www.w3.org/2005/xpath-functions");\n\n// Compile the XPath expression\nvar expr = XPathExpression.Compile("//function[starts-with(fn:lower-case(@name), '" + txtSearch.Text + "')]");\nexpr.SetContext(manager);\n\n// Evaluate the expression\nvar results = navigator.Select(expr);