// Ensure threads are properly stopped when your application exits\nThread thread = new Thread(MyThreadMethod);\nthread.IsBackground = true; // Background threads exit when the main thread exits\nthread.Start();\n\n// Alternatively, if you need to manually stop threads:\nprivate static ManualResetEvent _stopEvent = new ManualResetEvent(false);\n\nprivate static void MyThreadMethod(object state)\n{\n    while (!_stopEvent.WaitOne(TimeSpan.FromSeconds(1)))\n    {\n        // Perform thread work here\n    }\n}\n\n// To stop the thread\n_stopEvent.Set();