using System.Threading;\n\n// Create a new thread to play the sounds\nThread soundThread = new Thread(PlaySounds);\nsoundThread.Start();\n\nvoid PlaySounds()\n{\n    SoundPlayer soundPlayer = new SoundPlayer();\n\n    // Play the first sound\n    soundPlayer.SoundLocation = "sound1.wav";\n    soundPlayer.PlaySync();\n    pictureBox1.Invoke(new MethodInvoker(() => pictureBox1.Image = Image.FromFile("image1.jpg")));\n\n    // Play the second sound\n    soundPlayer.SoundLocation = "sound2.wav";\n    soundPlayer.PlaySync();\n    pictureBox1.Invoke(new MethodInvoker(() => pictureBox1.Image = Image.FromFile("image2.jpg")));\n\n    // Play the third sound\n    soundPlayer.SoundLocation = "sound3.wav";\n    soundPlayer.PlaySync();\n    pictureBox1.Invoke(new MethodInvoker(() => pictureBox1.Image = Image.FromFile("image3.jpg")));\n}