using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        var query = "SELECT NumberOfCores, NumberOfLogicalProcessors FROM Win32_Processor";\n        var searcher = new ManagementObjectSearcher(query);\n\n        foreach (var item in searcher.Get())\n        {\n            Console.WriteLine($"Number Of Cores: {item["NumberOfCores"]}");\n            Console.WriteLine($"Number Of Logical Processors: {item["NumberOfLogicalProcessors"]}");\n        }\n    }\n}