using System.Security.Cryptography;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Load the certificate\n        X509Certificate2 cert = new X509Certificate2("path/to/your/certificate.pfx", "password");\n\n        // Sign the assembly\n        Assembly assembly = Assembly.GetExecutingAssembly();\n        byte[] signature = SignAssembly(assembly, cert);\n\n        // Save the signed assembly\n        using (FileStream fs = new FileStream("signed_assembly.exe", FileMode.Create))\n        {\n            fs.Write(signature, 0, signature.Length);\n        }\n    }\n\n    static byte[] SignAssembly(Assembly assembly, X509Certificate2 cert)\n    {\n        // Create a new SHA256 hash\n        HashAlgorithm hashAlgorithm = HashAlgorithm.Create("SHA256");\n        byte[] hash = hashAlgorithm.ComputeHash(assembly.GetRawBytes());\n\n        // Create a new signature\n        RSAPKCS1SignatureFormatter formatter = new RSAPKCS1SignatureFormatter(cert.PrivateKey);\n        byte[] signature = formatter.CreateSignature(hash);\n\n        return signature;\n    }\n}