// Sync example\npublic enum ResultCode { OK, Fail }\n\npublic ResultCode SendMessageSync(string message)\n{\n    try\n    {\n        // Send message and process response\n        if (responseCode == "OK")\n            return ResultCode.OK;\n        else\n            return ResultCode.Fail;\n    }\n    catch (Exception ex)\n    {\n        // Log the exception\n        throw; // Re-throw the exception\n    }\n}\n\n// Async example\npublic void SendMessageAsync(string message, Action<Error, ResultCode> callback)\n{\n    try\n    {\n        // Send message and process response\n        if (responseCode == "OK")\n            callback(null, ResultCode.OK);\n        else\n            callback(null, ResultCode.Fail);\n    }\n    catch (Exception ex)\n    {\n        // Log the exception\n        callback(ex, ResultCode.Fail);\n    }\n}