/// <summary>\n/// Gets or sets the value of the element.\n/// </summary>\n/// <remarks>The value must match the regular expression: ^[a-zA-Z0-9]+$</remarks>\n[RegularExpression(@"^[a-zA-Z0-9]+$")]\npublic string ElementValue\n{\n    get { return _elementValue; }\n    set\n    {\n        if (!Regex.IsMatch(value, @"^[a-zA-Z0-9]+$"))\n        {\n            throw new ArgumentException("Invalid element value", nameof(value));\n        }\n        _elementValue = value;\n    }\n}