using (TransactionScope scope = new TransactionScope())\n{\n    try\n    {\n        // Create user in third-party application using Process.Start\n        using (Process process = Process.Start("createuser.exe", "arguments"))\n        {\n            process.WaitForExit();\n            if (process.ExitCode != 0)\n            {\n                throw new Exception("Error creating user");\n            }\n        }\n\n        // Update your application database\n        using (SqlConnection connection = new SqlConnection("your_connection_string"))\n        {\n            connection.Open();\n            // Perform updates\n        }\n\n        scope.Complete();\n    }\n    catch (Exception ex)\n    {\n        // Rollback changes in your application database\n        using (SqlConnection connection = new SqlConnection("your_connection_string"))\n        {\n            connection.Open();\n            // Perform rollback operations\n        }\n\n        // You may also want to delete the user created in the third-party application\n        using (Process process = Process.Start("deleteuser.exe", "arguments"))\n        {\n            process.WaitForExit();\n        }\n\n        throw;\n    }\n}