// Assuming 'dataTable' is your DataTable object\n\n// Add a new column to the DataTable\nDataColumn newColumn = new DataColumn("NewColumn", typeof(string));\ndataTable.Columns.Add(newColumn);\n\n// Iterate through each row in the DataTable\nforeach (DataRow row in dataTable.Rows)\n{\n    // Check the value of Column 1\n    string column1Value = row["Column1"].ToString();\n\n    // Set the value of the new column based on the value of Column 1\n    switch (column1Value)\n    {\n        case "A":\n            row["NewColumn"] = "G";\n            break;\n        case "B":\n            row["NewColumn"] = "H";\n            break;\n        case "C":\n            row["NewColumn"] = "I";\n            break;\n        default:\n            // Handle other cases or set a default value\n            break;\n    }\n}