// In your child control (.ascx.cs)\npublic event EventHandler EditButtonClicked;\n\nprotected void EditButton_Click(object sender, EventArgs e)\n{\n    if (EditButtonClicked != null)\n    {\n        EditButtonClicked(this, EventArgs.Empty);\n    }\n}\n\n// In your parent page (.aspx.cs)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    // Bind the event handler to the child control's event\n    EditControl.EditButtonClicked += EditControl_EditButtonClicked;\n}\n\nvoid EditControl_EditButtonClicked(object sender, EventArgs e)\n{\n    // Update the visibility and enabled state of other child controls\n    AddControl.Visible = false;\n    ListControl.Visible = false;\n    EditControl.Visible = true;\n    EditControl.Enabled = true;\n}