using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        const int numElements = 10_000_000;\n\n        // Array\n        var arr = new int[numElements];\n        var sw = Stopwatch.StartNew();\n        for (int i = 0; i < numElements; i++)\n        {\n            arr[i] = i;\n        }\n        Console.WriteLine($"Array initialization: {sw.ElapsedMilliseconds}ms");\n\n        // List\n        var list = new List<int>(numElements);\n        sw.Restart();\n        for (int i = 0; i < numElements; i++)\n        {\n            list.Add(i);\n        }\n        Console.WriteLine($"List initialization: {sw.ElapsedMilliseconds}ms");\n    }\n}