// Using ANTS Profiler\nusing RedGate.ANTS;\nclass MyWinFormsApp\n{\n    [STAThread]\n    static void Main()\n    {\n        var profiler = new Profiler();\n        profiler.StartProfiling();\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MyMainForm());\n        profiler.StopProfiling();\n    }\n}