using Microsoft.Azure.KeyVault;\nusing Microsoft.Azure.KeyVault.Models;\n\n// Create a Key Vault client\nvar keyVaultClient = new KeyVaultClient(new DefaultAzureCredential());\n\n// Create a new key\nvar key = new KeyBundle\n{\n    Key = new Key\n    {\n        Kty = "oct",\n        Value = Convert.ToBase64String(Encoding.UTF8.GetBytes("your_secret_key_here"))\n    }\n};\n\n// Store the key in Key Vault\nvar keyBundle = await keyVaultClient.SetKeyAsync("https://your-key-vault.vault.azure.net/", "your_key_name", key);\n\n// Retrieve the key from Key Vault\nvar retrievedKeyBundle = await keyVaultClient.GetKeyAsync("https://your-key-vault.vault.azure.net/", "your_key_name");\n\n// Use the retrieved key for encryption and decryption\nvar encryptionKey = Convert.FromBase64String(retrievedKeyBundle.Key.Value);