// In your child control (.ascx.cs)\nprotected void EditButton_Click(object sender, EventArgs e)\n{\n    // Update ViewState or SessionState\n    ViewState["EditMode"] = true;\n}\n\n// In your parent page (.aspx.cs)\nprotected void Page_PreRender(object sender, EventArgs e)\n{\n    if (ViewState["EditMode"] != null && (bool)ViewState["EditMode"])\n    {\n        // Update the visibility and enabled state of other child controls\n        AddControl.Visible = false;\n        ListControl.Visible = false;\n        EditControl.Visible = true;\n        EditControl.Enabled = true;\n    }\n}