// Create a new role\nvar role = Sitecore.Security.Accounts.Role.FromName("sitecore\\roles\\myrole");\n\n// Create a new role to be a member of the first role\nvar childRole = Sitecore.Security.Accounts.Role.FromName("sitecore\\roles\\mychildrole");\n\n// Add the child role to the parent role\nrole.Roles.Add(childRole);