using System.Web.Caching;\n\npublic class DataManager\n{\n    public Data[] GetData()\n    {\n        string cacheKey = "MyDataCacheKey";\n        object cachedData = Cache[cacheKey];\n\n        if (cachedData == null)\n        {\n            // Do SQL to get data\n            Data[] data = GetDataFromDatabase();\n            Cache.Insert(cacheKey, data, null, DateTime.Now.AddSeconds(10), Cache.NoSlidingExpiration);\n            return data;\n        }\n        else\n        {\n            return (Data[])cachedData;\n        }\n    }\n}