using Microsoft.ReportingServices.ReportRendering;\n\n// Define a custom report item class\npublic class BoxPlotReportItem : ReportItem\n{\n    // Render the report item\n    public override void Render(ReportRendering reportRendering)\n    {\n        // Create a new chart\n        Chart chart1 = new Chart();\n\n        // Set the chart type to Custom\n        chart1.ChartType = ChartType.Custom;\n\n        // Define the Box Plot series\n        Series series1 = new Series();\n        series1.ChartType = SeriesChartType.BoxPlot;\n        series1.Name = "BoxPlotSeries";\n\n        // Add data points to the series\n        series1.Points.Add(new DataPoint(10, 20, 30, 40, 50));\n        series1.Points.Add(new DataPoint(20, 30, 40, 50, 60));\n\n        // Add the series to the chart\n        chart1.Series.Add(series1);\n\n        // Customize the chart's appearance\n        chart1.XAxis.LabelsVisible = true;\n        chart1.YAxis.LabelsVisible = true;\n\n        // Render the chart\n        chart1.Render(reportRendering.GetGraphics());\n    }\n}