SshTransferProtocolBase scp = new Scp(SSH_HOST, SSH_USER);\nscp.Password = SSH_PASSWORD;\nscp.KeepAlive = true; // Set KeepAlive to true\nscp.Timeout = 30000; // Increase timeout to 30 seconds\nscp.ConnectTimeout = 15000; // Increase connect timeout to 15 seconds\nscp.Connect();\nforeach (string file in files)\n{\n    string remotePath = "incoming/" + new FileInfo(file).Name;\n    scp.Put(file, remotePath);\n}\nscp.Close();